-- ----------------------------
-- Table structure for album
-- ----------------------------
DROP TABLE IF EXISTS `album`;
CREATE TABLE `album` (
  `album_id` int NOT NULL AUTO_INCREMENT COMMENT '专辑编号',
  `singer_id` int NOT NULL COMMENT '歌手编号',
  `name` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT '专辑名称',
  `time_of_issue` datetime DEFAULT NULL COMMENT '发行时间',
  `company` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT '发行公司',
  `introduce` varchar(10240) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT '介绍',
  `img` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT '专辑封面',
  `type_id` int DEFAULT NULL COMMENT '种类id',
  PRIMARY KEY (`album_id`),
  KEY `singer_id` (`singer_id`),
  KEY `type_id` (`type_id`),
  CONSTRAINT `album_ibfk_1` FOREIGN KEY (`singer_id`) REFERENCES `singer` (`singer_id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `album_ibfk_2` FOREIGN KEY (`type_id`) REFERENCES `album_type` (`type_id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=34 DEFAULT CHARSET=utf8mb3 COMMENT='Album专辑实体类';

-- ----------------------------
-- Records of album
-- ----------------------------
INSERT INTO `album` VALUES ('1', '2', '不如吃茶去', '2014-08-23 17:16:28', '海蝶', '关于爱恨情仇的道理，关于人生的感悟，我们说了那么多，也听了那么多，最后又如何？\r\n\r\n有诗云:空持百千偈，不如吃茶去。这是前人流传下来的禅机，是许嵩心向往之的怡然与从容，更是他第五张全创作专辑希望传达的生活态度。\r\n\r\n大千世界，多得是想不通的事，多得是猜不透的心，多得是看不透的人。不如不想，不如不猜，不如不看，不如寄情山水，不如快意人生，不如，吃茶去。——许嵩\r\n\r\n在前四张全创作专辑收获了一长串的乐坛奖项与听众认可之后，许嵩对待演艺工作依然保持着若即若离的状态，为创作留足空间与时间。近两年的时光，除了常规演出之外，他选择了将生活的大部分内容转向旅行，读书，进修，创作摄影随笔集，以及创作制作本张专辑。“不如吃茶去”像一句随性的口头禅，而这份悠游自在，正是他真实的生活状态。他是音乐人之中的文人，还是文人之中的音乐人，或许这些定义对他而言已不重要。他的平和、干净、低调，是经过了时间检验的个人特质，这样的个性注定与娱乐绝缘，但又恰恰是乐迷最为欣赏的特质。本辑中充满中国古典气息的词作再次展现了许嵩强大的笔力与深厚的古典文学修养。近两年许嵩常在大山大河间旅居，所到一处觉得清凉自在了，就小住十天半月，吃茶，读书，抚琴，会友，呼吸山岚清气，远离喧闹的都市文明，以更为清净的心去体悟生活，由此创作出一系列清润如茶的音乐作品。本辑中有诸多精彩佳句均是在幽居之时的灵光闪现。当回到北京，回到严谨的工作状态时，他第五次掌控全局，身兼本专辑音乐总监、全部词曲的创作者、制作人的身份，更亲力亲为操刀编曲与混音工作。从创作词曲开始，到编曲、监制编曲，再到现场录制各样乐器，完成混音与母带制作，每首作品平均包含两百多轨乐器素材，总计近两千轨，均由许嵩一手包办制作，在工作室内醉心本辑制作的总时间超过一千五百小时，全身心为音乐注入情感和能量。\r\n\r\n《不如吃茶去》是充满碰撞火花的一体：东方的清音雅乐，西方的律动节奏，交织并进；披着古色古香的外套，内里却是一颗极为现代的精神内核；既书写怡然洒脱的态度，也不掩内心的感伤离愁，生活五味熔于一炉。遣词造句是许嵩式的美学，不经意间抖落的词趣与禅机耐人寻味。你眼前浮现的是一个老成的少年，有故事，但绝不沧桑，他低吟浅唱着把他的故事讲给你听，无论故事是悲或喜，都讲得饶有情致，作者的迷人之处也恰在这里。这样的音乐让人难于定义，它像是属于从今天到过去几百年中的任何一个时段：古典与流行交织，让听者在不知不觉间忘却了所处的时代。\r\n\r\n作为一张穿梭古今的作品，《不如吃茶去》集结了多位优秀的中国民乐演奏家：古筝、竹笛、箫、尺八、埙、古琴、二胡、马头琴等乐器，各路大师以教材级别的绝妙演奏为专辑增光添彩。但精彩并未到此为止，许嵩在本辑中的乐思并不停步于中国民乐，而是以丰富多元的音乐语言表达专辑概念。于是，我们在《弹指一挥间》中聆听中国国家交响乐团恢宏而深沉的弦乐演奏；在《梧桐灯》里邂逅世界上最古老的乐器都都克笛 （Duduk）如泣如诉的天籁之音，与Trip-Hop节奏融为一体；《等到烟火清凉》着意于氛围渲染，渐进铺陈情绪，高潮充满震撼力，八字唱词禅意深远；《山水之间》里一段古筝与爵士吉他齐奏，行云流水般交相辉映，演奏技巧在流行乐中甚为罕见；《隐隐约约》将迷幻的电子音色与马头琴、失真吉他奇妙混搭，氛围诡谲奇异,彰显许嵩卓绝的编曲想象力；《惊鸿一面》与魅力歌姬黄龄深情演绎令人心向往之的古典式爱情；《宇宙之大》以印度传统乐器西塔尔琴作点缀，营造虚空幻境；《有桃花》加入传统民歌采样，温馨小曲令人动容……随处可见的精妙构思匠心独运，加上华语乐坛顶级阵容的编曲搭档们倾力助阵，《不如吃茶去》注定成为不可错过的精品佳作。\r\n\r\n在音乐之外，本辑的MV由台湾著名导演黄中平先生操刀。MV拍摄生涯超过二十年、拍摄过数百位歌星的他破天荒接下了同一张专辑中的三首MV拍摄工作以及部分摄影工作，上山下海旅途奔波长达数十日，背负超大的工作量只为完美呈现许嵩作品中超凡脱俗的意境。本辑的内页写真则由知名摄影家张罗平先生拍摄，作为曾与许嵩多次合作的伙伴，张罗平的摄影精准展现许嵩的个性特质。本辑的装帧设计则邀请到曾入围格莱美奖的台湾顶级设计师萧青阳老师出手，简约大气的设计风格毫无造作修饰，贴合专辑返璞归真的音乐主题。', 'music/2/1/1.jpg', '1');
INSERT INTO `album` VALUES ('2', '2', '不语', '2016-03-03 17:16:28', '海蝶', '晴朗不语 阴天不语\r\n\r\n坚强不语 脆弱不语\r\n\r\n那些习惯沉默不语的人\r\n\r\n内心的火焰可能更炽烈\r\n\r\n \r\n\r\n2016最新悬疑影片《不速之客》，汇集黎明、耿乐、韩彩英等重量级演员而成为年度极受期待的电影，力邀许嵩跨刀演绎电影主题曲《不语》。\r\n\r\n \r\n\r\n《不语》由火星电台创作，如诗般简洁而深远的歌词，钢琴与弦乐搭配的古典质感，契合影片意境。片方听过歌曲小样后第一感觉邀请许嵩来演绎会极为合适，然而工作人员上网搜索资料发现许嵩几乎只演唱其自己原创的作品，于是就放弃了这个想法。随后的时间里，片方邀约了其他几位歌手试音，歌手不约而同表示这首歌写得看似简单，实则非常难于演绎：副歌不断重复追问，如何表达的有层次但不落俗套是个难题。试音后发现效果不太理想。抱着试试看的态度，片方联系了许嵩所属唱片公司，没想到许嵩素来对悬疑电影题材非常喜爱，愿意尝试演绎本影片主题曲，双方一拍即合。\r\n\r\n \r\n\r\n许嵩除歌手身份外，更以词曲创作而为听众所知晓，作品题材丰富，是集词、曲、唱及音乐制作于一人的全能型音乐人。而此次不动笔墨仅以歌声出演，便将作品中的那份隐秘心事娓娓道来：寂寥，隐忍，迷走，自我追问……逐层推进，丝丝入扣。沉稳而细致的演绎，独占了一份只属于许嵩的个人风味。', 'music/2/2/2.jpg', '1');
INSERT INTO `album` VALUES ('3', '2', '书香年华', '2016-02-25 17:16:28', '海蝶', '《书香年华》本是应央视邀请而创作的主题音乐，意在鼓励小朋友们勤读经典书籍，进而弘扬中华民族传统文化，许嵩也以轻快的曲风并带着自我调侃的笔触完成了这首新歌，打造出不带说教味又充满正能量的歌曲，令央视的编导们耳目一新，非常喜爱，并将这首作品推荐给了猴年春晚，虽然遗憾未能在春晚舞台表演新歌，但在2016中央电视台元宵晚会，许嵩再次受到邀请演绎这首作品，并创意性的增加跨界合作，邀请著名演员孙涛合唱。孙涛多次登上央视春晚表演小品节目，实力笑星的加盟也为这首非常正能量的歌曲增加了节庆的轻松氛围。', 'music/2/3/3.jpg', '1');
INSERT INTO `album` VALUES ('4', '2', '今年勇', '2016-12-22 17:16:28', '海蝶', '近来发行第六张创作专辑《青年晚报》及单曲《江湖》大获好评后，许嵩接着发行了最新单曲作品《今年勇》。\r\n\r\n常言道：“好汉不提当年勇。”那什么才值得取得过战绩的好汉们提上一提？许嵩造了个词儿作答——今年勇。前奏一响起便显露出全新气象，好莱坞配乐风格的弦乐与中国大鼓齐头并进，中西合璧，富有故事性。随性的演唱，也契合了歌词里所表达的那份洒脱：站在今时，立足当下，面对新的挑战要拿得出勇气与冲劲，而不是吃老本抑或轻易地激流勇退。\r\n\r\n许嵩笔下，复古风味的佳作颇多，但为人称道的是他在这类复古作品里所探讨的主题非常开阔：《庐州月》的思乡，《半城烟沙》的止战，《清明雨上》的悼亡，《江湖》的侠骨，《南山忆》的隐逸，《山水之间》绘景，《伴虎》的君臣之道，《惊鸿一面》的你侬我侬……各有各的精彩。《今年勇》亦是在考究的字句中见功夫，例如，“醉庆同袍沙场归”的酣畅之后，整个人感到“清风皓月任我飞”，寥寥数语便道尽豪杰之士的那份落拓不羁；短短一首词有四处使用否定句式：“不见得”、“不代表”、“不足道”、“功成身不退”，引出一系列全新的反向思考；开篇道：“功名桥，世俗道，年少难免走一遭”，接着话锋一转：“笑的也不见得逍遥”——取得功名业绩并不等于幸福。这样的弦外之音奠定了作品的格局——今年要勇，明年要勇，老去亦要勇，这份勇并不是为世俗功名而战，而是人在每一个阶段都应该活出足够的生命强度。', 'music/2/4/4.jpg', '1');
INSERT INTO `album` VALUES ('5', '2', '千古', '2015-08-20 17:16:28', '海蝶', '故事的甘或是苦\r\n\r\n如果没有你 何人可诉\r\n\r\n流芳或是贻笑千古\r\n\r\n如果没有爱 有何不同\r\n\r\n \r\n\r\n许嵩作品《千古》，由许嵩词曲创作及制作。有别于上张专辑里《山水之间》式的华丽古韵，许嵩在《千古》里擦除了年代感，以干净利落、清朗质朴的音乐语言，描摹对爱情的感伤与眷恋，也营造了更为开阔的赏味格局。词作方面，微微带有文言气息的现代文法，细腻与洒脱并存，字里行间满溢许嵩的个人风格，见字如面。\r\n\r\n \r\n\r\n2015年许嵩凭借热门专辑《不如吃茶去》收获了多个创作大奖，而除了重大典礼活动外则鲜少露面，绝大部分时间埋首于他的新专辑制作工作，这引发了部分乐迷的“不满”，连连在他的微博“催专”、求新歌。据了解新专辑制作顺利进行中，预计于第四季度发表。', 'music/2/5/5.jpg', '1');
INSERT INTO `album` VALUES ('6', '2', '半城烟沙', '2010-04-11 17:16:28', '安徽音像', '许嵩2010年独立创作发行的专辑《寻雾启示》正在街头巷尾热力传播着，而眼下《半城烟沙》又已席卷而来，一会儿烟一会儿雾的许嵩总是让人捉摸不定。他每每下笔写复古题材，总是会把准一个文化脉象，无一不是历史文化诉之不尽的文化命题。而这首《半城烟沙》则把思辩的主题落到了「战」与「和」之上，别具禅意。音乐方面，强节奏+独门唱法已经成为招牌；不愠不火、不动声色的演绎，以悢懒的帝观姿态静待一场一触即发的厮杀，愈加凸显战争的冰冷残酷，而作者的反战立场也尽在歌中了。独特的视角、悲悯的情怀、含蓄内敛的表现手法，很难不给人留下深刻印象。', 'music/2/6/6.png', '1');
INSERT INTO `album` VALUES ('7', '2', '天龙八部之宿敌', '2011-09-20 17:16:28', '海蝶', '2011年金秋时节。在加盟海蝶后，逐渐显露天王气质的许嵩，不仅新专辑销量一路高歌，更是以天价数字接下了搜狐畅游公司旗下最新网络游戏《天龙八部3》的年度代言人。基于该公司上一代产品主题曲《半城烟沙》就出自许嵩之手，并且大获成功。这款拥有无数玩家基础的网络游戏最新代主题曲的打造，也非许嵩莫属。这次许嵩依旧中国风词曲全创作，并领衔林哲民、蔡庭贵、等两岸乐坛主心骨。一起合力为新歌加持力量。也使得该歌还未首播，就获得了无数乐迷的翘首期待。', 'music/2/7/7.jpg', '1');
INSERT INTO `album` VALUES ('8', '2', '如果当时2020', '2020-11-11 17:16:28', '', '', 'music/2/8/8.png', '1');
INSERT INTO `album` VALUES ('9', '2', '寻宝游戏', '2018-07-12 17:16:28', '海蝶', '2018年许嵩最新作品《寻宝游戏》，许嵩第7张词曲全创作专辑。\r\n\r\n \r\n\r\n你我他她一生在世，寻寻觅觅各有所求，人人心中皆有秘密宝藏。许嵩在《寻宝游戏》中以松弛、真实的笔触书写对生活的体悟以及对世态的思考，寻访人心、玩味世情。\r\n\r\n \r\n\r\n《寻宝游戏》的语言系统中充满着多义与辨证，让读者在聆听的过程中不知不觉沉浸于许嵩构建的这一场头脑游戏，富有诗性与灵气的文字挑战歌词创作新高度。他的文字里时而带着严谨理性的刚硬质地，但细听之下又可体会他的幽默与柔软——这份既刚又柔、亦庄亦谐的矛盾感形成了某种难以言喻的、专属于许嵩的艺术效果。\r\n\r\n \r\n\r\n在《寻宝游戏》里许嵩以极致复古的音乐语言带领听者回到20世纪60-80年代，融合Blues-Rock、Country-Rock、Folk-Rock、Electronic-Rock、Jazz等根源性音乐风格，加以最新锐的制作理念打造当代音乐精品，既复古，又超前。他旧时明月般的老灵魂，总是能找到极其鲜活的表达方式来完成情感与观点的输出。整张专辑音乐类型多元化却又统一于许嵩强大的音乐制作和统筹功力，在听觉层面达到了惊人的一体性。许嵩以松弛自然的演唱方式唱出独属于他的腔调与态度，无可拷贝。音乐里精心设置的诸多细节伏笔也有待听者去开掘玩味，这是《寻宝游戏》所引发的寻宝游戏。\r\n\r\n \r\n\r\n《寻宝游戏》的平面相片及音乐录影带拍摄于美国纽约及美国西海岸地区。二十余人团队同许嵩一起出发，海外拍摄花费长达半个月的时间。专辑封面相片拍摄于美国西海岸地区1号公路，牛仔青年，开启寻宝之旅。以橙色为主色的美式唱片装帧，在复古中加入摩登元素，设计考究。专辑曲目《老古董》、《大千世界》、《如约而至》的音乐录影带摄于纽约，由台湾著名MV导演比尔贾执导，影像画面寓意丰富，余味悠长。\r\n\r\n \r\n\r\n《寻宝游戏》专辑概念由许嵩一手打造，并且每首曲目的词曲创作、音乐制作均由许嵩独自完成。至今为止许嵩已一人作词、作曲、演唱、制作、策划7张个人专辑，这份成绩与其全方位的音乐才华在华语乐坛是独树一帜的。2018年是许嵩进入公众视野的第十二年，成功发行7张词曲全创作专辑以及近40首原创单曲，勤勉专注于音乐创作；屡获国内外各大华语音乐颁奖典礼奖项，拥有突破十亿次的数字音乐下载量，影响力强大；2017年开启万人巡演，场场爆满座无虚席，从头至尾全程万人大合唱；在斐然的成绩面前，许嵩始终远离喧嚣保持低调姿态，只靠作品说话，在《寻宝游戏》里进一步展现了广阔的音乐视野与深厚的人文积淀。', 'music/2/9/9.jpg', '1');
INSERT INTO `album` VALUES ('10', '2', '寻雾启示', '2010-01-06 17:16:28', '华宇世博', '2009，2008，2007……你可能在街头听过许嵩的歌，在各种音乐排行榜上见过许嵩这个名字，看到过年轻人一脸兴奋的讨论着“Vae”、“自定义”，但你不一定知道，这不是什么唱片公司打榜、宣传所产生的效果，而是完全靠着乐迷口口相传的力量，这股力量已经延续了近三个年头，并且正在持续蔓延中。\r\n\r\n他拒绝签约唱片公司，独立完成个人专辑的创作、制作及发行。他绝对是内地近年来网络人气最高的独立音乐人，没有之一。他是内地乐坛的一个异数。\r\n\r\n2010年1月发行的最新作品《寻雾启示》是他的个人第二辑。和2009年1月发行的首张专辑《自定义》一样，他一人担任了整张唱片的作词、作曲及制作人——大到整张专辑的风格定位，小到每一首歌的和声编配、乐器音色选用、录音混音，大大小小所有环节都在他掌控之中。\r\n\r\n听许嵩的创作，总能让人体会到前所未有的音乐灵感，总能让人在细枝末节处找到直击人心的感动。在这张《寻雾启示》中，刚柔并济的曲风搭配、率性洒脱的行文作词，自居一格的演唱方式，都让人感受到一种无可拷贝的气场。这是一张游走在「复古 / 新锐」、「嬉皮 / 深情」、「回忆 / 现实」、「深度 / 流传度」之间的作品，对以上要素恰到好处的分寸把握，再一次证明了23岁的他已经拥有强大制片功力。\r\n\r\n乍听到“寻雾启示”这个名字，还以为是寻物启事。其实，我们每个人都在生活中寻找着，又在寻找中生活着；身边很多看似美丽的事物和雾一样，看得见却让人抓不住；所谓人生的进阶与历练，可能正是要在经历得失与悲喜之后，方能得到启示。\r\n\r\n听《寻雾启示》，是非常容易陷入某种情绪的。你要知道，音乐一旦对了味，俘获人心的力量有多大。\r\n\r\n01.叹服 词/曲：许嵩\r\n\r\n招牌式黑色作品。爱情里的所有忠诚与信仰化作一片雾气，让天下多少有情人在一路追寻过后，只留下阵阵唏嘘。\r\n\r\n02.灰色头像 词/曲：许嵩\r\n\r\n当一段本身已足够伤感的回忆被挖掘到了极致，却发现所有的回忆竟是一团雾，一场梦……充满回忆感的吉他滑音、电钢solo，慵懒而不失弹性的深情演绎，是为慢板抒情R&B之上品。\r\n\r\n03.我无所谓 词/曲：许嵩\r\n\r\n玩世不恭却又透出几分认真的一首RAP，那种嬉皮劲儿是能让人会心一笑的。\r\n\r\n04.庐州月 词/曲：许嵩\r\n\r\n作者化身一位古代出仕者，思考着功名利禄的虚妄、感叹着物是人非的苍凉、抒发着远离故乡的怅惘，可谓各种细腻熔于一炉。慢板的6/8R&B节奏引领出一个个章节，一幅幅古典画面自然而然呈现在你眼前。\r\n\r\n05.不煽情 词/曲：许嵩\r\n\r\n名为不煽情，但却很是煽动人心的一首叙事小品。许嵩从头至尾采用了口语对话的笔法，极富表现力的双吉他演奏陪伴着歌声，描摹出一场爱情的淡然落幕。\r\n\r\n06.我们的恋爱是对生命的严重浪费 词/曲：许嵩\r\n\r\n歌名很长很直白，歌曲也非常直截了当。热烈却不轻浮的Rock编曲以及随性奔放的演唱都很令人惊艳，亦让人领略到许嵩的多面。\r\n\r\n07.白马非马 词/曲：许嵩\r\n\r\n以哲学命题为名展开思考。大胆采用纯钢琴伴奏+几近清唱的混音方式，细枝末节如此凸显，连每一声换息都牵动人心。\r\n\r\n08.单人旅途 词/曲：许嵩\r\n\r\n失恋者快意洒脱的单身宣言，往往都带着些酸；“自由”不过就是一团雾，一个幌子，远远看起来很美，真正身处其间拥有了，却又觉着寂寞。这首中板R&B里面的情味比起它里面的转音，更为复杂。\r\n\r\n09.在那不遥远的地方 词/曲：许嵩\r\n\r\n梦想就在不遥远的地方——虽然，在得到之前，我们并不能确定它是否是幻想。电子音色与中国民乐混搭、开阔大气的词风、行云流水般的光滑人声、多层次对话式和声恰到好处的运用，组成了这首极具实验性的跨界混搭励志曲。', 'music/2/10/10.png', '1');
INSERT INTO `album` VALUES ('11', '2', '小烦恼没什么大不了', '2013-05-20 17:16:28', '海蝶', '自推出上张全创作大碟取得骄人成绩后，许嵩就一度被歌迷“催生”新作品，本次推出的新歌《小烦恼 没什么大不了》先放出片段给歌迷“解馋”，却反倒吊起歌迷胃口，大呼只听片段一点也不过瘾，还纷纷猜测许嵩接下来会用怎样的方式与歌迷互动。', 'music/2/11/11.png', '1');
INSERT INTO `album` VALUES ('12', '2', '情侣装', '2008-01-15 17:16:28', '', '年仅21岁的网络红人Vae（许嵩），在现身网络的两年内，凭借《七号公园》《浅唱》《玫瑰花的葬礼》等大量原创作品受到海量网友的喜爱，屡获各大门户网站评选出的最佳网络男歌手、最佳词曲作者等大奖。曾荣获罗马尼亚国际通俗音乐比赛亚军，亚洲音乐节通俗音乐比赛新人大奖。值得一提的是，Vae不仅精通作词、作曲、编曲，所有作品的录音与后期混音工程也均由其独立完成，可见Vae全方位的音乐能力与极高的音乐素养。 VAE对《别咬我》这首歌的看法 物是人非的时候，回忆就会显示它可爱而又残忍的一面。虽然生活中的事一件接着一件，欢畅的，莫名的，卑劣的，让人应接不暇，但是记忆总是会自动筛选出某些事，供我在日后抚摩把玩，这些事，被叫做典故。最近发生了一些事，直接触发我记忆的阀门，典故如洪水一般涌出，让我在这样的转凉时节，有些不知所措。', 'music/2/12/12.png', '1');
INSERT INTO `album` VALUES ('13', '2', '我乐意', '2018-04-19 17:16:28', '海蝶', '近日，许嵩忙于第七张个人创作专辑的最后阶段工作，\r\n\r\n与此同时推出全新单曲《我乐意》，\r\n\r\n作为新专辑发布前的暖身礼物，回馈歌迷。\r\n\r\n《我乐意》由许嵩包揽词曲创作及制作。\r\n\r\n响指、五把吉他与淡淡的电子音色一齐营造出清新校园味道；\r\n\r\n回归到了至简至朴的情书式歌词，\r\n\r\n比如“你的笑像西瓜最中间那一勺的口感”，令整首作品漫溢着恋爱清甜；\r\n\r\n许嵩独有的慵懒随性的演绎方式，\r\n\r\n一如歌词里所言——“我绝不会深情款款”，\r\n\r\n令整首歌听来甜而不腻，甜度恰到好处。\r\n\r\n许嵩在个人APP里发帖戏称这一次的创作是“老来俏”，\r\n\r\n可见“玩音乐”的开放心态始终不改，各种曲风信手拈来。 \r\n\r\n在歌曲制作环节许嵩亦亲力亲为，制作出多个版本反复比较，\r\n\r\n最终发行版本删减了若干乐器声部，\r\n\r\n“让这首歌听起来简洁轻盈才对味”，许嵩这样说。\r\n\r\n在混音时许嵩有意对歌曲高频部分进行细微降频处理，\r\n\r\n令整首作品听起来更加温暖无刺激，适合循环播放。\r\n\r\n母带处理环节则放弃了在音量响度方面的竞争，\r\n\r\n以80’s唱片的音量标准来处理这首歌的母带，\r\n\r\n以此换取了歌曲动态起伏的弹性。\r\n\r\n每个环节精雕细琢，只为打造出词、曲、唱、制作四位一体的优质音乐作品。', 'music/2/13/13.jpg', '1');
INSERT INTO `album` VALUES ('14', '2', '我想牵着你的手', '2008-04-22 17:16:28', '唯亿文化', '《我想牵着你的手》 曾被改编为联想乐Phone广告背景音乐', 'music/2/14/14.png', '1');
INSERT INTO `album` VALUES ('15', '2', '放肆', '2020-10-12 17:16:28', '海蝶音乐', '许嵩最新作品《放肆》于2020年10月12日零点发行，由许嵩作词、作曲及制作。\r\n\r\n此次的国风新作以豪放不羁的姿态呈现，散发出不同于许嵩任何前作的独特气息。许嵩以极具创造性的细腻笔触在百余字内构建描摹一位孤身独心的浪迹之人在塞外酒馆中的放肆故事。吞吐日月的落拓豪情，在半醉半醒间烧燃起心中火焰。\r\n\r\n《放肆》的创作制作周期前后横跨了四个月，对许嵩来说这并不寻常，期间工作人员不断打听进度，但得到许嵩回复：“还请再等等。”结果成品出来后大家兴奋不已：超燃的节奏律动，粗粝而酣畅的演绎方式，戴好耳机听《放肆》，会不由自主踏出六亲不认的放肆步伐！\r\n\r\n《放肆》是游戏《天龙八部》邀约许嵩打造的第四首主题单曲，而前三首历经十年的时间跨度，已成为乐迷们耳熟能详的经典，时间检验出真正的优秀创作者。许嵩总在不断开拓创作边界，多元化的个人音乐风格展现了强大的创作力。而无论是《放肆》的侠骨，还是《宿敌》、《雨幕》的柔情，这些风格迥异的作品都统一于许嵩具有极强辨识度的个人语言系统，统一于许嵩心中的武侠宇宙。\r\n\r\n单曲主题封面由摄影家张罗平拍摄。面对多年合作伙伴的镜头，许嵩轻松释放个性中放肆不羁一面，很快便顺利拍完。对于这次特别的曲风，封面如何拍摄才能画龙点睛是个难点，因为如果穿上古装、完全重现歌中场景，则不够吻合音乐里武侠与摇滚乐融合、东西合璧的独特质感。最终大家决定以自然而然的方式表现出“放肆”之感就是最好的。许嵩更是临时请工作人员找来一个油灯摆在手边，之后还设法让火焰看起来大到离谱，以示“不是省油的灯”。', 'music/2/15/15.jpg', '1');
INSERT INTO `album` VALUES ('16', '2', '断桥残雪', '2007-12-28 17:16:28', '海雷音乐', '《断桥残雪》是由许嵩作词作曲并演唱的歌曲。', 'music/2/16/16.png', '1');
INSERT INTO `album` VALUES ('17', '2', '梦游计', '2012-07-11 17:16:28', '海蝶', '这城市\r\n\r\n太多形色匆匆的人\r\n\r\n有太多事情要做……\r\n\r\n那追逐 那较量 那欲望\r\n\r\n是在梦里都放不下的我执\r\n\r\n谁都自以为看透了现世\r\n\r\n但谁又真的想离开\r\n\r\n如果白昼注定以奔忙来填满\r\n\r\n梦中就请以畅游来救赎\r\n\r\n邀你参与，一场精心策划的出离。\r\n\r\n——2012/7/11 · 许嵩第四部作品「梦游计」\r\n\r\n浮华的现代都会，人与人、事与事之间千丝万缕的纠葛，使得白天总在上演着一场场追逐欲望的脑力较量。但回归到一个人的深夜，可否大胆借梦游一场，作为逃离一切束缚的妙计？在梦游的领域，抛开压力与顾虑，站在自己之外看自己，重新审视自己与他人、自己与世界的关系——超现实主义作品《梦游计》将提供这样的可能性。作者以敏锐的洞察力，天马行空的想象，出其不意的运笔，自成一派的音乐理念，邀你完成一场梦游式的出离。\r\n\r\n第四部作品《梦游计》，许嵩依然包揽十首词曲的创作、担当制作人及唱片统筹，用一整个25岁锤炼独立音乐作品新范本，再一次展现了惊人的音乐能量。后期环节中携手许环良、林哲民、蔡庭贵、杨阳、陈伟伦等一线配乐人与混音大师，在他的工作室磨合切磋了上百个日夜，精益求精，只为上演完美“梦游计”。视觉方面，平面写真融入超现实主义元素，以虚实相生的表现手法呈现“梦游”情境；MV奔赴台湾打造专属于《梦游计》的梦游诡谲风格；比尔贾、黄中平、方亮等众多视觉创意高手前来加盟，联手奉上独具梦游滋味的眼球盛宴。你将领略这样的许嵩，嬉皮又文雅的，迷离又真实的，戏谑感十足却充满诚意的。\r\n\r\n许嵩说，整部作品的创作概念本就源于一场梦。他在那梦里游走，超脱于现实，却得以更真实的触碰现实；他把他所历经的一切，摊开来给你看。\r\n\r\n＊ 全碟十首作词+作曲+制作，许嵩第四次展现强大音乐能量\r\n\r\n＊ 2009-2012连续发表四张词曲全创作专辑，笔耕不辍，佳作连连\r\n\r\n＊ 2011年凭《苏格拉没有底》成为内地唱片销量王，获得国内外共计9项音乐大奖\r\n\r\n如果你对他的音乐还不了解，那么《梦游计》是一场很好的初见；如果你是他的音乐追随者，那么《梦游计》将第四次印证你的选择。\r\n\r\n＊单曲介绍\r\n\r\n01. 胡萝卜须\r\n\r\n法国黑色童话《胡萝卜须》里的那个红头发少年，盯着自己亲手摔碎的鼹鼠，任由它的血花在空中飞扬……\r\n\r\n你未必没有暗暗释放过——扭曲的、荒诞的、隐藏在你道德外衣下的暴力。\r\n\r\n因为你一定暗暗接收过——傲慢的、偏狭的、让你愤懑却也令你成长的眼神。\r\n\r\n谁 可记取几桩童年旧梦\r\n\r\n谁 又深藏几段残破青春\r\n\r\n谁 能说自己不是胡萝卜须\r\n\r\n《胡萝卜须》由许嵩作词、作曲及制作，是他第四部独立作品《梦游计》的开篇序曲。作为大陆词人中的新锐标杆人物，许嵩向来以笔法的大胆与视角的独特见长。此次新歌以法国文学名作《胡萝卜须》的主人公为切入点，运笔穿梭于梦样童话与冰冷现实之间，在“自我”与“胡萝卜须”两端来回切换，令人读罢笑中有泪；音乐方面，许嵩创作极具西洋色彩的半音阶旋律线，并创意性的融入探戈节奏，在手风琴与萨克斯的呼应之下自然而然地散发出异域风情；标志性的“懒人唱腔”此番亦演绎至极致，梦游般慵懒而又充满戏谑的独特诠释，令《胡萝卜须》的黑色幽默在短短的三分多钟里无限扩张。词、曲、唱、制的四者合一，给他的作品打上了鲜明的个人烙印。知名导演比尔贾掌镜MV，以寓意巧妙的视觉语言展现胡萝卜须的精神世界，打造出专属于《梦游计》的超现实主义诡谲风格。\r\n\r\n02. 幻听\r\n\r\n「 在远方的时候 / “又想你到泪流” / 这矫情的措辞结构 / 经历过的人会懂 」\r\n\r\n你是否幻听过你的前度在你耳边低语？\r\n\r\n当你发现那不过是一场幻觉，不过是自己大脑制造出的假相——你是否仍还希望那假相能持久一些？\r\n\r\n很多时候，回归现实真不如躺在幻觉里永不醒来……\r\n\r\n真诚质朴的遣词造句，精致纯美的旋律，感性细腻的演绎，给人带来最原始的感动。配乐方面，一把吉他、几声鼓点，淡淡的抒情慢板R&B曲风，纯粹到极致，也深情到极致；知名MV导演黄中平与许嵩再度合作，以幻梦风格呈现他笔下的这场“极致爱情”。\r\n\r\n“如果一个人被魔鬼深深凝视过了，那么他的眼睛就会瞎掉。\r\n\r\n如果一个人被这首歌轻轻带入了，他的耳朵会幻听。”\r\n\r\n03. 对话老师\r\n\r\n「 我写检讨的文采不到写情书的一半 / 你做学问的本领有没五柳先生一半 」\r\n\r\n淘气的闯祸，早恋的情愫，焦急盼望的下课声，害怕家长发现的不及格考卷……念书时发生过很多让自己脸红的故事吧？\r\n\r\n但，那不正是长大后的心中最美好的那些年？\r\n\r\n许嵩梦回学生时代，信手拈来一曲校园小品，幽青春一默，也幽老师们一默。\r\n\r\n04. 伴虎\r\n\r\n「 谁能以为 / 同朝可同寐 」\r\n\r\n历史的车轮碾过，多少英雄马到功成，多少亡魂哀怨嗟叹？\r\n\r\n功高震主的人，杀身之祸总是难免。\r\n\r\n而对于“主子”而言，事后文过饰非颠倒黑白，比起杀戮来，更是拿手好戏。\r\n\r\n至于仅存在于少数人心中的真相，随着时间推移，终会飘散无踪……\r\n\r\n许嵩全新创作中国风，跳出当下一切流行套路。梦游旧朝，明写君臣关系暗抒历史观，意旨深远。如果说作品前半段还是他标志性的慵懒与忧伤，后半段则令人拍案惊喜：多把失真吉他的层叠渐进，将格局骤然扩张；撕裂式的高音演唱，彻底抛开声乐传统；第四分钟之后甚至飚出男高音真声罕有企及的超高音“Hi C#”，将悲悯情绪发泄到极致。MV导演陈映之为让画面体现歌曲深沉质感与磅礴大气，大手笔启用直升机航拍，壮阔大海、层峦崖石、平沙落日、幽秘森林，一一全景式呈现。\r\n\r\n05. 闺蜜\r\n\r\n「 闺蜜一出手 / 真让你难受 / 现实教会了人要往前游 / 却弄丢朋友 」\r\n\r\n“闺中密友”不是应当简作闺密吗？为何新新人类惯用“闺蜜”？\r\n\r\n可能换了个“蜜”字，就像是能增加几分同甘共苦的甜蜜感吧。\r\n\r\n可许嵩笔下的这对闺蜜，有不同的故事。小小说般层层推进情节，用词精炼老道；结尾话锋一转，升级立意。配乐怀旧却又带着鲜明的节奏律动，这是专属于他的音乐特质。\r\n\r\n他写闺蜜关系，君臣关系，师生关系，……他度量着人与人之间的温度，用他温暖的腔调捂热现实的冰冷。\r\n\r\n06. 装糊涂\r\n\r\n「 对于你的谎话我装糊涂 / 装糊涂是极难的艺术 / 稀里糊涂是我的包容 」\r\n\r\n在爱情里，一切的质疑都是不必要的。因为，如果不能接受，干脆趁早分开；如果不想分开，那就装糊涂吧，不然就算鱼死网破得到了真相，又能有什么更好的结果？\r\n\r\n——但要小心，某一个清晨醒来也许会发现，自以为的温存竟是梦游般的幻觉，现实中早已一无所有！\r\n\r\n装糊涂，装到真的糊涂了……\r\n\r\n许式经典R&B，逐层递进的配唱方式，摇摆感性的节奏律动，把这首歌曲推送到每个“糊涂着却不糊涂”的人心中。2012暑期档影片《影子富豪》制片方在第一次听到歌曲小样时，就立马邀此歌作为电影主题曲。\r\n\r\n07. Play with style\r\n\r\n何谓Play with style？抛开潮流的诱惑，突破名相的束缚，活出真自我，找到自己的态度是为先！此作品除了歌词层面传达了这一意旨，音乐方面也极具先锋性：世界民族乐器的大量融入，给音乐增添了一份神秘的色彩——但却又辅以西洋感的R&B鼓点、极前卫的弹性演唱方式——这种古今碰撞所产生的特殊质感，恐怕只有许嵩能独家定制。\r\n\r\n08. 心疼你的过去\r\n\r\n「 忽然你哭了 / 问你在哭什么 / 你哭的更狠了 / 我好像明白了 」\r\n\r\n每个人都有过去，但不是每一段过去都能轻易过得去。\r\n\r\n你若爱Ta，你便很可能纠结于Ta的过去；Ta若爱你，便很可能对你隐瞒Ta的过去。\r\n\r\n如果时间是一条河……你是否愿意在梦里游回到岁月上游，去更早的时间点和Ta相遇，为Ta拦下所有的伤害？\r\n\r\n许嵩此作采用日记体写作风格，整篇没有任何诗意斧凿，用最真实的笔触把最纠结的心疼刻画到血肉里，令人惊心。循环渐进式的R&B Loop编曲与深情款款的念唱，逐层铺排出幽诡阴郁的氛围。从《伴虎》的激越磅礴，到《对话老师》的顽皮十足，再到《幻听》的传统深情，直到这一首的Rap……许嵩作为创作人以及演唱者，在《梦游计》这张专辑中展现了对多种曲风的极佳驾驭力，使得此专辑一经推出无论在广度还是深度上都备受瞩目。\r\n\r\n09. 全球变冷\r\n\r\n这是一个全球化的时代，这是一个地球村。但不知道从什么时候开始，繁盛的背后培育起冷漠，GDP的上扬伴随着道德的滑坡，丰富的物质世界背后是精神世界的逐渐荒芜……我们是该坐视人与人之间冰山横陈、任凭全球变冷，还是用微笑温暖彼此？许嵩此作写于“小悦悦”事件之时，不为问责，只愿同样的悲剧能少一些，人与人之间的关怀能多一些。\r\n\r\n10. 亲情式的爱情\r\n\r\n「 我有多想多想再看一眼 / 你最后那天在我身边 / 那熟悉的脸 ……」\r\n\r\n是否电光石火般的爱恋，最终都会归于平淡？当曾经的日思夜想辗转反侧魂不守舍，沦为一起吃饭睡觉买菜吵架做家务，恐怕就再也没有给对方写一封情书的雅兴了……\r\n\r\n但，爱是消失了，还是悄悄转化了存在形式？\r\n\r\n许嵩这首作品的创作，是感于新闻里一件真人真事。一对中年夫妻遭遇了一场严重车祸，丈夫卡在车轮下难以救出，他停止心跳前对救援人员的最后一句话是，别管我，先救我爱人！说完便再也没有睁开眼睛。侥幸生还的妻子在事后哭着接受采访说，就在车祸发生前一分钟，他们还在为家庭琐事吵嘴，吵得很凶，而前一阵还在闹离婚。……\r\n\r\n珍惜身边人，让爱从日常生活里流淌出来，不要等到失去才追悔，一辈子并没有想象之中那么长。', 'music/2/17/17.jpg', '1');
INSERT INTO `album` VALUES ('18', '2', '江湖', '2016-08-31 17:16:28', '太合音乐', '何谓江湖？\r\n\r\n在众人眼中，大概是刀光剑影，大概是恩怨情仇。\r\n\r\n许嵩的《江湖》又是怎样？\r\n\r\n \r\n\r\n伴随着淅沥小雨、悠远笛声和忽然惊醒梦中人的一记古筝，听者跟随主人公潜入暗夜——飞檐上，雨滴滑落连接天地；暖阁中，美人抱琴半遮面。“今夕是何夕”？“故地是何地”？两句不知是嗟叹还是反问的唱词，便廓清了他与一切剑拔弩张的关系，曾经的江湖落拓，曾经的鲜衣热血，都已风干飘零，即便“飞鸽来急”，也要兀自“沉默江湖里”。\r\n\r\n \r\n\r\n音乐演绎方面，经典的传统民乐与全新电子乐器绝妙融合，复古情怀与时代感兼得。两段间奏里中国大鼓与HIPHOP电鼓的交织并进令人拍案，更大胆使用唢呐、锣等等极难在流行乐中运用的中国元素，构建出一派刀光剑影、名利杀场的浮华江湖，以此与主角的出离隐遁形成了反差，令人印象深刻。与许嵩以往一些较为“柔情”的中国风作品相比，《江湖》更偏于描摹“侠骨”，用词极简，旨意却更为深远。“敬属江上雨/寒舟里/我独饮”——作品以一句“我独饮”作结，人声从恢弘的伴奏中抽离出来，一如主角从纷乱的江湖中抽身一般，告别前尘往事，悠然归隐。\r\n\r\n \r\n\r\n这首歌作为网剧《画江湖之不良人》的主题曲，与剧目的内容也极为贴合。《画江湖之不良人》中的主角李星云就是一个“衣锦夜行”之人，便如许嵩在歌中营造出的江湖一般，恩怨散去、刀剑归隐。后期制作方面，编曲由著名音乐人郑楠操刀，层次细腻丰富，功力毕现；总制作人、混音师、录音师、母带工程师一如既往的全部由许嵩本人亲自担任，全情投入到每一个音乐细节里，精益求精，确保了作品的一流水准。', 'music/2/18/18.jpg', '1');
INSERT INTO `album` VALUES ('19', '2', '深夜书店', '2017-03-14 17:16:28', '海蝶', '深夜街角书店\r\n\r\n散落着摇摇的灯火\r\n\r\n穿越纸张一面\r\n\r\n是你半边清秀侧脸\r\n\r\n时间滴答淌过\r\n\r\n想说什么该怎么说\r\n\r\n \r\n\r\n创作才子许嵩 x 虚拟歌姬洛天依\r\n\r\n白色情人节惊喜相遇\r\n\r\n跨越次元献音乐重磅\r\n\r\n最好的偶然\r\n\r\n是与你不期而遇\r\n\r\n \r\n\r\n许嵩操刀创作倾心Jazz-Hiphop\r\n\r\n洛天依破壁次元浪漫对唱\r\n\r\n治愈系声线里装满甜蜜的词不达意\r\n\r\n演绎冬末春初闪烁烂漫的怦然心动\r\n\r\n不经意的回眸是我刻意安排的邂逅\r\n\r\n深夜书店里 有你 你是美好地', 'music/2/19/19.jpg', '1');
INSERT INTO `album` VALUES ('20', '2', '温泉', '2020-05-20 17:16:28', '海蝶音乐', '骑车漫游泉水小镇\r\n\r\n夏日旅途 情意升温\r\n\r\n爱如温泉般 彼此疗愈\r\n\r\n许嵩最新作品《温泉》于2020年5月20日发行，由许嵩作词作曲及制作。以《温泉》为题，书写爱的疗愈与温柔的力量。旋律线如同林间小路般蜿蜒游走，直到抵达丛林深处那一池藏在心底的寂静温泉。歌曲蕴含的情味是丰富的：一方面是美好欢愉的当下时刻，另一方面笑容背后的往事不必触及，未来亦包含着不确定性。暖调与冷调交织的微妙情绪，既贡献出全新的许式温情甜话，也道出温泉之底的潜流心语。\r\n\r\n演唱方面，女声部分由新锐歌手刘美麟献声演绎，许嵩亲自坐镇为其录音并一手操办歌曲制作，为乐迷奉上了不可多得的男女对唱新作。', 'music/2/20/20.jpg', '1');
INSERT INTO `album` VALUES ('21', '2', '素颜', '2010-08-18 17:16:28', '唯亿文化传播', '这首是与何曼婷一起合作的新单曲，新节奏，R&B曲风，依然还是VV的风格。。。轻快。。', 'music/2/21/21.png', '1');
INSERT INTO `album` VALUES ('22', '2', '绝代风华', '2018-11-22 17:16:28', '华宇世博', '《绝代风华》单曲介绍 2018继发行第七张个人全创作专辑《寻宝游戏》后，许嵩受邀为游戏《天下3》创作出全新主题曲《绝代风华》，暌违数年的“许氏中国风”再现江湖。 \r\n\r\n《绝代风华》的基调是逍遥自在的：主人公曾“鲜衣怒马”、“征战杀伐”，有了所爱之人后便告别江湖、悠然归隐，与知己落棋听雨、朝夕为伴、互赏风华。特别之处是开篇以悬疑气氛入题，描摹不速之客在林间暗伏之景——许嵩用寥寥数语就为笔下人物创造出一种“隐逸中带有少许凶险”的生活状态，让人物鲜活起来。生动的叙事性、严谨的逻辑性、造词与用典的准确性，这三者是许嵩国风类词作的一贯特性。\r\n\r\n 音乐方面，许嵩的国风音乐一贯强调律动与节奏，以电鼓与民族大鼓相交叠的方式，让听感自由穿梭于中西之间，毫不拘谨；古筝琵琶笛子与失真吉他熔于一炉，古今交错，擦除时代感。主副歌的旋律创作采用由快到慢、由短句到长句的对比手法，呼应词里由愉悦喝茶下棋到深情回忆往事的情绪变化，丝丝入扣。词、曲、唱、制作紧密咬合，达到浑然一体的程度。', 'music/2/22/22.jpg', '1');
INSERT INTO `album` VALUES ('23', '2', '羡慕', '2020-01-02 17:16:28', '华宇世博', '许嵩2020最新单曲作品《羡慕》正式发行，由许嵩词曲创作及制作。新年新单曲以一则夏日故事给寒冬带来暖意。\r\n\r\n“我走进电影院看一部冷门的戏” ，曲中人去影院看冷门戏，顺带看到观众火热戏，激起一串内心戏，不知有无反转戏。时间从入场前到散场后，场景由户外到室内再到户外，影院里的“冷”气引发“暖”心举动继而形成连锁反应，许嵩以精巧的布局谋篇和生活化的诗歌笔触来书写一桩观影小事，书写心中的情爱冷暖。而那些多义的词句更是赋予了这首作品极大的想象弹性，构成了《羡慕》独特的艺术效果。\r\n\r\n作曲与编曲中分寸适宜地融入了蓝调音乐风格，将词中淡淡的浪漫、暗暗的幽默与生活的闲情精准地呈现出来。独具一格的悠然旋律不急于抢夺人耳，却以丰富而耐听的细节攻占人心。洒脱随性的演唱为这首作品注入了鲜活而灵动的生命力。音乐制作精致简洁，令人能够静下心来反复聆听回味。\r\n\r\n是羡慕的还是被羡慕的，是旁观者还是亲历者，是纪实还是虚构，是赞美还是揶揄，是触摸到了情爱的真相还是坐井观天，这些设问和歌中男女的故事走向一起，让《羡慕》成为了一道海市蜃楼般亦真亦幻的风景。', 'music/2/23/23.jpg', '1');
INSERT INTO `album` VALUES ('24', '2', '自定义', '2009-01-10 17:16:28', '华宇世博', 'Vae（许嵩）首张独立创制专辑《自定义》，1月10日正式发行。近年来在网络上凭借多首原创作品及独特个人气质一直人气高涨的Vae，包揽了《自定义》的作词、作曲、编曲、演奏、制作乃至录音、混音等一切工作。12月11-21日，在其官方网站十日预售量即突破一万张，可谓树立了独立音乐人的人气典范。 \r\n\r\n22岁的Vae，在他的《自定义》里彻头彻尾的实现了自己主宰音乐，定义自我。有如此独当一面的能力的华语歌手本就不多，其中用四个月就能从无到有的交出这样一张优秀唱片的人更少，而交出之后有这么多人趋之若鹜的热议和支持而不是孤芳自赏——并且，这一切都是在没有经纪公司宣传推广的前提下，实在可以说是稀奇了。 \r\n\r\n我们也向你诚意推荐这张风格独特、听感舒适的《自定义》。 \r\n\r\n独立营造每一个音乐细节，使得整张专辑充满了浓郁的个人气息。Vae冷色调的感情逻辑贯穿始终，复古和流行碰撞的旋律线条随处可见，全方位的音乐能力展露锋芒。《内线》、《多余的解释》、《如果当时》、《坏孩子》、《城府》等曲目，有强烈的叙事感与自嘲意味，加上若隐若现的黑色幽默，和当下热门大碟里或甜腻或悲痛或忧伤或劲爆的音乐都不同的是，《自定义》里每首作品都好比一出悲喜剧——难以对其定义，难以给其归类，但又有血有肉，惹人玩味。这样既让人有听头、又让人有想头，既有流行潜力、又保持了创作个性的华语歌曲确实为数不多。 \r\n\r\n《自定义》的感情色彩是灰色的，暗涌的回忆与潜藏的自我调侃交替更迭，在情绪处理上有着超越年龄的成熟和细腻。创作者Vae也似乎压根没有打算让整张专辑的曲风变化多端，摆成一道拼盘——如果几年后《自定义》还被很多人提起，那么这一点一定是个重要因素——风格上的协调性被驾驭的极为出色，但又绝对让人一遍听下来就记得住每首歌的特质。歌词题材方面，亦在小范围内做了大文章，回归情歌本质，以内心体验和情感故事为主，不过也有如《内线》这样涉及杀手暗战的边缘题材让人耳目一新。音乐制作方面，Vae简洁而soft的音乐编配安静舒适，个人辨识度与耐听度很高。当捧着歌词册聆听与玩味《自定义》一遍之后，相信你能感知到它独特的音乐份量。 \r\n\r\n没有商品化的定位和包装，没有企宣团队的策划和炒作，这张独立唱片也没有像很多初次发片的新人一样为了标榜独特而去大肆玩弄风格流派上的概念。不过，以Vae的网络知名度而言，虽然是初次发片但也算不得新人了。这张自居一格的《自定义》，蕴含着让人欣喜的极大可能。', 'music/2/24/24.jpg', '1');
INSERT INTO `album` VALUES ('25', '2', '苏格拉没有底', '2011-04-01 17:16:28', '海蝶', '『苏格拉底：我知道一件事，那就是我什么都不知道』\r\n\r\n如果你一脸问号，不解许嵩何许人也，证明你已经开始落伍了。\r\n\r\n许嵩，85后原创音乐人，2009年独立出版首张词曲全创作专辑《自定义》，2010年独立出版第二张词曲全创作专辑《寻雾启示》，两度掀起讨论热潮，并均在唱片发行一个月内售罄。\r\n\r\n他的音乐自成一个世界，却总是巧合般地与这个时代连动，吸引着越来越多年轻人的关注。\r\n\r\n他在百度、腾讯等门户歌手排行榜上常年稳居前十位；他的作品在中国移动彩铃榜上是畅销冠军；他的作品随便挑几首都可累计破亿的试听量；他的《灰色头像》、《叹服》等作品是年轻人的流行圣经，以至于在发表一年后仍然雄踞排行榜；他的个人论坛Vaecn.com截至此刻已有41万铁杆会员；他的贴吧、微博等网络阵营常年人气旺盛；他的词曲作品尤以作词视角的把握独步江湖，素有音乐怪才之称。\r\n\r\n当你给男/女朋友打电话听到对方的彩铃时，当你打开电视听到入耳的广告歌时，当你打开电脑随机播放热门歌曲列表时……你在生活里，早已与他悄然相遇过了。\r\n\r\n思考者的梦\r\n\r\n『苏格拉底：未经思考的生活是不值得过的』\r\n\r\n从昔日那个写着哼着《城府》夺得各大音乐试听榜冠军的音乐顽童成长到今时，许嵩在音乐层面一直保持独立做派，此番再度包揽十首词曲的全部创作。毫无粉饰的生存哲学，超越现实的想象思辨，细而不腻的私人回忆，在十首作品里尽数展现。自成一派的制作风格与词曲质感，随性慵懒的演绎方式，标志性的假音与转音，舒服惬意的中慢板R&B节奏……令人一听便可品出强烈的个人风味。浪漫与幽默并存、务实与务虚交织，《苏格拉没有底》仿若暗器般无色无味却能迅速在人心中稳固沉积，在悠然自得的节奏摆荡中持续扩散。', 'music/2/25/25.jpg', '1');
INSERT INTO `album` VALUES ('26', '2', '蝴蝶的时间', '2017-11-30 17:16:28', '海蝶', '梁祝化蝶，是千百年来为人们传颂的爱情绝唱，可谓家喻户晓。无论是诗词、戏曲、音乐、电影，都不止一次地演绎着这段故事。此次许嵩受邀为连续剧《梁山伯与祝英台新传》创作的主题曲，不同于之前梁祝题材的音乐作品，独辟蹊径地化身蝴蝶，以蝴蝶的视角来回首那段经典传说。\r\n\r\n \r\n\r\n作为创作人与音乐制作人，许嵩对每一首原创作品的打磨都力求既坚持个人风格又推陈出新，于细节处见功夫。钢琴前奏刚响起便连续多次转调，带来新鲜听感的同时营造出游走与不确定的氛围，如同窥见那坠入时空黑洞的蝴蝶。大量电子乐器与电子鼓的穿梭使用，打破所谓“复古”与“流行”的界限，和他笔下的歌词一样，在古今语汇里自由穿行，擦除年代印记与风格标签。如“等到月影斑驳/等到满城花落”呼应开篇“花开不败/香满楼台”，以超现实的意象来表现时间的诡谲流速；副歌一句“百年后”，进而“千年后”，倏地拉伸了故事的时空维度；钢琴演奏或柔曼如雾、或铿锵如铁，与琵琶的缠绵悱恻交替前行，让这首歌在角徵宫商之外多了一份跨越时空的厚重质感——不以世人的时间为尺度，而以蝴蝶的时间为线索。\r\n\r\n \r\n\r\n许嵩向来秉持玩音乐、享受音乐的心态，辅之以严谨的工作态度，打造了一首首极具个人风格的全新作品。目前，第七张全创作专辑也正式进入制作期。', 'music/2/26/26.jpg', '1');
INSERT INTO `album` VALUES ('27', '2', '违章动物', '2013-08-22 17:16:28', '海蝶', '沉静外表下 一束炽热而犀利的目光\r\n\r\n许嵩2013年度单曲——《违章动物》\r\n\r\n作词+作曲+制作+编曲+录音+混音+唱=许嵩，一个人的音乐世界\r\n\r\n2013对于许嵩来说是沉淀的一年。在做完了年初的不插电演唱会之后，许嵩请辞了几乎所有的演艺工作，转而投身旅行，阅读，音乐进修，创作，出版个人摄影随笔集《海上灵光》……许嵩说，短暂的抽身并不是贪图安逸，而是得以用客观清醒的视角，重新梳理忙忙碌碌的日常工作，重新审视自己的音乐，进而找寻出更本我的音乐表达方式。\r\n\r\n最新单曲《违章动物》的音乐风格充满许嵩的个人特质。平稳有力的鼓点律动，清冷而迷离的乐器音色编配，慵懒随性不带斧凿的唱腔，不动声色的铺陈出一个关于“违章动物”的故事。他在编曲中引入世界音乐的元素，如开篇与结尾的原生态唱段采样以及少数民族原声乐器，这些民族元素与西洋鼓点Loop冲撞出一种时空错位的奇异氛围，而与之呼应的是，歌词里古今穿梭的遣词造句，亦是在时空错位中完成的一轮许嵩式的抒发。连说带唱的演绎方式，戏谑劲儿拿捏的恰到好处：多一分则偏向玩世不恭，少一分则流于严肃刻板。\r\n\r\n《违章动物》的意旨，听者与作者或许心照不宣，又或许各有解读。他在作品里一直体现这样的态度：做言之有物、有血有肉的音乐。社会生活里的大小事件，随时都可能成为他音乐创作的题材。然而在具体的创作手法上，许嵩又绝不局限于就事论事、一味地去表达同情或批判，而是探索着更多的表达可能性：他把对于社会生活的关注以一种设身处地的视角来陈述，以一种诗化的语言来发想，从而避免了悲天悯人式的说教感或愤青发泄式的苍白感，体现出了艺术的魅力。\r\n\r\n某种程度来说，许嵩又何尝不是一个不折不扣的“违章动物”：在他的音乐里，他从来不是按章法出牌、循着通常流行路数行走的音乐人。这首《违章动物》，也可视作他为各路“不合规则”的违章动物们做了一场真情代言。', 'music/2/27/27.jpg', '1');
INSERT INTO `album` VALUES ('28', '2', '送你的独白', '2007-12-04 17:16:28', '', '', 'music/2/28/28.png', '1');
INSERT INTO `album` VALUES ('29', '2', '通关', '2017-06-30 17:16:28', '海蝶', '打一场比赛，我们过关斩将，力求通关。\r\n\r\n玩一个游戏，我们全情投入，期待通关。\r\n\r\n谈一回恋爱，分分合合，持久作战，只为修成正果，早日通关。\r\n\r\n过一场人生，经历多少悲欢洗礼，天人交战，最终才能活的明白通透，顺利通关？\r\n\r\n……\r\n\r\n天下事，事事皆是修炼，时时都在通关之路上。我把这些细碎的念头揉捻成了《通关》这首歌。\r\n\r\n——许嵩\r\n\r\n近来许嵩忙于“青年晚报”演唱会的巡回演出，然而新专辑与新单曲的创作与打磨也在同步进行中，毫无懈怠。此次最新发表单曲《通关》，亦是一首值得反复玩味的音乐作品。曲目以强劲的鼓点律动打开局面，行云流水般的旋律，六把吉他的精彩演绎，加上许嵩随性的唱腔，“通关”的畅快之感展露无遗。歌词里考究的细节不胜枚举，例如：“我感谢自己平凡，敢爱敢恨没负担”；“我感谢自己不凡，可爱可恨都包揽”。相同的句式，仅换几个字，就将两种截然不同的人生轨迹勾勒出来，对比鲜明却又共存在一体之下，文趣丛生。\r\n\r\n《通关》由许嵩包揽作词作曲及制作，郑楠编曲，特别值得一提的是歌中每把吉他均由新加坡著名音乐家AdamLee先生弹奏，做出了教科书级别的精彩演绎。', 'music/2/29/29.jpg', '1');
INSERT INTO `album` VALUES ('30', '2', '雨幕（新天龙八部）', '2019-10-13 17:16:28', '海蝶音乐', '许嵩2019最新单曲《雨幕》，由许嵩包揽作词、作曲、制作及演唱。\r\n\r\n时隔一年未见新作，此次“飘然一曲”诱得广大乐迷“侧耳听”。潇潇的雨幕里，三日无言的福船上，饮酒、听曲、赏雨、忆人，然而故事却在有人卧于绯泊之中时戛然而止，故事表象下的诸多隐喻也浮出水面。独此一家别无分号的叙事笔法，布局谋篇像本逻辑缜密的小小说，起承转合间充满戏剧张力。文趣盎然的遣词造句，既有新颖的意象与丰富动人的细节，也有言而未尽的留白与隐喻。作为《新天龙八部》的主题曲，全篇只字未描写武打争斗，却在看似绘景的潇潇雨幕里暗藏着汹涌伏笔，为作品平添了悬疑色彩。\r\n\r\n音乐方面，简洁工整、含蓄隽永的旋律赏心悦耳，偶尔的离调所带来的曲折跌宕感配合歌词一起细品更是滋味无穷。强有力的鼓点节奏稀释了感伤的浓度，反而让整体的情绪变得更加微妙而耐听。有别于2018年大受好评的《寻宝游戏》的随性不羁，许嵩此次以沉稳内敛的唱腔来诠释新作。《雨幕》词、曲、制作、唱的一体性达到了紧密咬合无可分割的程度。', 'music/2/30/30.jpg', '1');
INSERT INTO `album` VALUES ('31', '2', '雪花谣', '2007-11-28 17:16:28', '海雷音乐', '', 'music/2/31/31.png', '1');
INSERT INTO `album` VALUES ('32', '2', '青年晚报', '2016-06-27 17:16:28', '海蝶', '◆2016年许嵩最新作品《青年晚报》，许嵩第6张词曲全创作专辑。\r\n\r\n◆《青年晚报》，是一个三十而立的青年敞开自己和你面对面交心，也是一家晚报记述着生活的真相和所引发的思考，感性与理性交叠。歌词读来有如摊开一张晚报所看到的版面：社会生活、人生况味、暖心故事、摄影专栏，甚至不缺关于“最佳歌手”的娱乐头条以及“早睡身体好” 的养生大法。闪耀着思维火花的文字，诗性与易读性兼具，偶尔透出冷冷的幽默感，是独属于许嵩的那份亦庄亦谐。\r\n\r\n◆《青年晚报》以宽阔的创作视野展现了多元化的音乐风格，而这一切又统一于许嵩的个人特质，丰富而不散乱。较多曲目以电钢、电吉他以及流动的电子乐音色为串联，铺陈出静谧氛围，一如唱片封面中行走在巴黎街头的青年，背靠夕阳，晚报时分。\r\n\r\n◆音乐制作方面，《青年晚报》由许嵩担任音乐总监并包办制作所有曲目。至今为止许嵩已经一人作词、作曲、唱、制作6张个人专辑，“一人包办制”使得他的专辑一贯具有极为强烈的个人风格。编曲及演奏方面，包括AdamLee、蔡廷贵、郑楠、高飞等来自新加坡、北京与台北的著名音乐人友情加盟，为《青年晚报》添彩。\r\n\r\n◆唱片的平面资料及《摄影艺术》的音乐录影带拍摄于法国巴黎。十余人团队同许嵩一起从北京出发，海外拍摄共花费长达半个月，由青年摄影家张罗平掌镜，真正实践“摄影艺术”。《雅俗共赏》及《最佳歌手》的音乐录影带由台湾金曲奖最佳导演比尔贾执导，用前卫的影像风格精准呈现歌曲内涵。\r\n\r\n◆2016是许嵩进入公众视野的第十年，十年内成功发行6张词曲全创作专辑以及近40首原创单曲，专注创作从未停歇前行的脚步；屡屡获得国内外各大华语音乐颁奖典礼奖项，每张专辑都拥有突破亿万次的数字音乐下载量，影响力持续发酵；从年少成名到引发争论再到才华终受广泛认可，十年里无论身处什么阶段许嵩始终保持低调姿态，而许嵩的庞大歌迷群体也从当初冲动狂热的少年转变为如今以成熟理性青年为主，可谓是共同成长的青春十年。', 'music/2/32/32.jpg', '1');
INSERT INTO `album` VALUES ('33', '2', '飞驰于你', '2018-09-07 17:16:28', '海蝶', '敦煌作为古代丝绸之路的重要节点，东西方文化在这里汇聚碰撞，更拥有着莫高窟、阳关、月牙泉等古迹名胜，是世界级的文化名城。近日QQ飞车手游推出敦煌主题版本，邀请许嵩创作并演唱全新主题曲《飞驰于你》。\r\n\r\n \r\n\r\n《飞驰于你》由许嵩包揽词曲创作及制作。因敦煌在古时即传入了西域文化，许嵩在作曲中特意融入西域音乐元素，以富有异域风情的曲调展现敦煌的神秘；偶尔点缀的转音又带有甘肃敦煌当地的戏曲特色，契合了敦煌作为多民族文化荟萃之地的特质；民族音乐鼓点与摇滚乐新鲜搭配，以强劲的节奏感展现飞驰于敦煌奇景时的快意。\r\n\r\n \r\n\r\n词作方面，对意象进行精心拣选，寥寥数语即构建出敦煌秘境。在展现飞驰时的速度与激情之外，以“你”指代敦煌，抒发对敦煌厚重历史的感慨，令整首作品轻松潇洒而又不失余味。\r\n\r\n \r\n\r\n近年来许嵩为大量热门游戏创作主题音乐，每一次的创作都实现了既贴合主题气质、又淋漓尽致展现个人风格，并且始终坚持创新、诚意十足，不断给乐迷带来全新的听觉体验。', 'music/2/33/33.jpg', '1');

