-- Table structure for attention_album
-- ----------------------------
DROP TABLE IF EXISTS `attention_album`;
CREATE TABLE `attention_album` (
  `attention_id` int NOT NULL AUTO_INCREMENT COMMENT '专辑关注id',
  `user_id` int NOT NULL COMMENT '用户id',
  `album_id` int NOT NULL COMMENT '专辑id',
  PRIMARY KEY (`attention_id`),
  UNIQUE KEY `uq_user_album` (`user_id`,`album_id`) USING BTREE,
  KEY `album_id` (`album_id`),
  CONSTRAINT `attention_album_ibfk_1` FOREIGN KEY (`user_id`) REFERENCES `user` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `attention_album_ibfk_2` FOREIGN KEY (`album_id`) REFERENCES `album` (`album_id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8mb3 COMMENT='Attention_Album专辑关注实体类';

