CREATE TABLE `attention_mv` (
  `attention_id` int NOT NULL AUTO_INCREMENT COMMENT 'mv关注id',
  `user_id` int NOT NULL COMMENT '用户id',
  `mv_id` int NOT NULL COMMENT '视频id',
  PRIMARY KEY (`attention_id`),
