  CONSTRAINT `attention_mv_ibfk_2` FOREIGN KEY (`mv_id`) REFERENCES `mv` (`mv_id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb3 COMMENT='Attention_Singer关注歌手实体类';

