-- ----------------------------
-- Table structure for attention_song
-- ----------------------------
DROP TABLE IF EXISTS `attention_song`;
CREATE TABLE `attention_song` (
  `attention_id` int NOT NULL AUTO_INCREMENT COMMENT '歌曲关注id',
  `user_id` int NOT NULL COMMENT '用户id',
  `song_id` int NOT NULL COMMENT '歌曲id',
  PRIMARY KEY (`attention_id`),
  UNIQUE KEY `user_id` (`user_id`,`song_id`) USING BTREE,
  KEY `song_id` (`song_id`),
  CONSTRAINT `attention_song_ibfk_1` FOREIGN KEY (`user_id`) REFERENCES `user` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `attention_song_ibfk_2` FOREIGN KEY (`song_id`) REFERENCES `song` (`song_id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb3 COMMENT='Attention_Song歌曲关注实体信息';

-- ----------------------------
-- Records of attention_song
-- ----------------------------

-- ----------------------------
-- Table structure for comment
-- ----------------------------
DROP TABLE IF EXISTS `comment`;
CREATE TABLE `comment` (
  `comment_id` int NOT NULL AUTO_INCREMENT COMMENT '歌词评论id',
  `id` int NOT NULL COMMENT '用户id',
  `song_id` int NOT NULL COMMENT '歌曲id',
  `content` varchar(512) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT '评论内容',
  `content_time` datetime DEFAULT NULL COMMENT '评论时间',
  `number` int DEFAULT '0' COMMENT '点赞数',
  PRIMARY KEY (`comment_id`),
  KEY `id` (`id`),
  KEY `song_id` (`song_id`),
  CONSTRAINT `comment_ibfk_1` FOREIGN KEY (`id`) REFERENCES `user` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `comment_ibfk_2` FOREIGN KEY (`song_id`) REFERENCES `song` (`song_id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb3 COMMENT='Comment歌曲评论实体信息';

-- ----------------------------
-- Records of comment
-- ----------------------------

-- ----------------------------
-- Table structure for favorite
