  `song_id` int NOT NULL COMMENT '歌曲id',
  PRIMARY KEY (`coll_id`),
  KEY `id` (`id`),
  KEY `song_id` (`song_id`),
  CONSTRAINT `favorite_ibfk_1` FOREIGN KEY (`id`) REFERENCES `user` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `favorite_ibfk_2` FOREIGN KEY (`song_id`) REFERENCES `song` (`song_id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb3 COMMENT='Favorite歌曲喜爱实体信息';

