  KEY `type_id` (`type_id`),
  CONSTRAINT `mv_ibfk_2` FOREIGN KEY (`song_id`) REFERENCES `song` (`song_id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `mv_ibfk_3` FOREIGN KEY (`type_id`) REFERENCES `mv_type` (`type_id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb3 COMMENT='Mv视频实体信息';

