-- ----------------------------
-- Table structure for song
-- ----------------------------
DROP TABLE IF EXISTS `song`;
CREATE TABLE `song` (
  `song_id` int NOT NULL AUTO_INCREMENT COMMENT '歌曲id',
  `song_title` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT '歌曲名称',
  `song_url` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT '歌曲路径',
  `album_id` int NOT NULL COMMENT '专辑id',
  `lyric` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT '歌词路径',
  `spectrum` varchar(2000) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT '歌曲频谱',
  `type_id` int DEFAULT NULL COMMENT '歌曲种类id',
  PRIMARY KEY (`song_id`),
  KEY `album_id` (`album_id`),
  KEY `type_id` (`type_id`),
  CONSTRAINT `song_ibfk_4` FOREIGN KEY (`album_id`) REFERENCES `album` (`album_id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `song_ibfk_5` FOREIGN KEY (`type_id`) REFERENCES `song_type` (`type_id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=106 DEFAULT CHARSET=utf8mb3 COMMENT='Song歌曲种类实体信息';

